mapper.select:
/* Banner.selectContents */
SELECT
BA_SEQ baSeq , DATE_FORMAT(BA_RGST_DT,'%Y.%m.%d') baRgstDt , F_USER_NM(BA_RGST_ID) baRgstId , BA_ATCH_FILE_ID baAtchFileId , BA_TITLE baTitle , BA_CONT baCont , BA_URL baUrl , BA_WINDOW baWindow
FROM T_BANNER WHERE BA_USE_YN='Y' AND BA_SEQ = #{baSeq}

/* Banner.selectList */
SELECT
BA_SEQ baSeq , DATE_FORMAT(BA_RGST_DT,'%Y.%m.%d') baRgstDt , F_USER_NM(BA_RGST_ID) baRgstId , BA_ATCH_FILE_ID baAtchFileId , BA_TITLE baTitle , BA_CONT baCont , BA_URL baUrl , BA_WINDOW baWindow
FROM T_BANNER WHERE BA_USE_YN='Y'
ORDER BY BA_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
/* Banner.insertContents */

	INSERT	INTO	T_BANNER(
	           	  	BA_SEQ				  											
					, BA_RGST_ID
					, BA_ATCH_FILE_ID 									
					, BA_TITLE													
					, BA_CONT 													
					, BA_URL 														
					, BA_WINDOW			
					, BA_EXPOSE_YN		 												 
					, BA_ORDER_NUM		   												 																							
	)     
	VALUES	(   
					(SELECT IFNULL(MAX(TS.BA_SEQ)+1,1) FROM T_BANNER TS)
					,#{loginSeq     }          
					,#{baAtchFileId }  
					,#{baTitle      }
					,#{baCont       }  
					,#{baUrl        }
					,#{baWindow     }
					,#{baExposeYn   }
					,#{baOrderNum   }
	)

mapper.update:
/* Banner.updateContents */

	UPDATE	T_BANNER       
	 
	   SET   
			BA_RGST_DT								 							 = NOW()
			, BA_RVSE_ID														 =#{loginSeq	}	
			, BA_ATCH_FILE_ID 													 =#{baAtchFileId}
			, BA_TITLE														     =#{baTitle     }
			, BA_CONT 															 =#{baCont      }
			, BA_URL 															 =#{baUrl       }
			, BA_WINDOW 														 =#{baWindow    }
		 	, BA_EXPOSE_YN														 =#{baExposeYn	}
			, BA_ORDER_NUM														 =#{baOrderNum  }
		 	      
	 WHERE	BA_SEQ = #{baSeq}

/* Banner.updateReverse */

	UPDATE	T_BANNER     
	        
	   SET   
			BA_RGST_DT								 							 = NOW()
			, BA_RVSE_ID														 =#{loginSeq	}	
			
		
			, BA_TITLE														     =#{baTitle     }
		
			
			
		
			, BA_CONT 															 =#{baCont      }
		
			    
			
		
			, BA_WINDOW 														 =#{baWindow    }
		
			
	
			, BA_USE_YN 														 = 'Y'
		      
	 WHERE	BA_SEQ = #{baSeq}

	/* Banner.deleteContents */
	   
		UPDATE	T_BANNER
	       SET   
          	  	BA_USE_YN='N'
		 WHERE 	BA_SEQ = #{baSeq}

	/* Banner.deleteBannerContents */
	   
		UPDATE	T_BANNER
	       SET    
          	  	BA_USE_YN='N'
		 WHERE 	BA_SEQ = #{col1}
