mapper.select:
/* Calendar.selectList */

	SELECT 
		  	DATE_FORMAT(CA_DATA_DATE, '%Y.%m.%d') 					caDataDate
			, CA_CONT 												caCont
			, CA_HOL_YN 											caHolYn
			  
	  FROM	T_CALENDAR
	 WHERE	DATE_FORMAT(CA_DATA_DATE, '%Y%m') = #{caSchYearMonth}
	   AND	CA_RGST_ID = #{loginSeq}
	   AND	CA_USE_YN = 'Y'    
  ORDER BY	CA_DATA_DATE

/* Calendar.selectContentsList */
SELECT CA_SEQ caSeq ,DATE_FORMAT(CA_DATA_DATE, '%Y.%m.%d') caDataDate ,CA_HOL_YN caHolYn ,CA_CONT caCont ,DATE_FORMAT(CA_RGST_DT, '%Y.%m.%d') caRgstDt
FROM T_CALENDAR WHERE CA_DATA_DATE = #{caDataDate} AND CA_USE_YN = 'Y'

mapper.update:
/* Calendar.deleteContents */
   
	UPDATE	T_CALENDAR
	   SET	CA_USE_YN = 'N'
	 WHERE	CA_SEQ = #{caSeq}

mapper.insert:
	/* Calendar.insertContents */
	
        INSERT  INTO	T_CALENDAR
				( 
			    CA_DATA_DATE  
			    , CA_CONT
			    , CA_RGST_ID 
			    , CA_HOL_YN
			    )          
			             
	    VALUES	(	   
			  	DATE_FORMAT(#{caDataDate},'%Y-%m-%d')
				, #{caCont}
				, #{loginSeq}
				, IFNULL(#{caHolYn}, 'N', 'Y')
				)

mapper.update:
/* Calendar.updateContents */
 
	UPDATE	T_CALENDAR    
	   SET	 
	   		CA_CONT 								= #{caCont}
			,CA_DATA_DATE 							= #{caDataDate}
			,CA_HOL_YN 								= NVL(#{caHolYn}, 'N')
			     
	 WHERE	CA_SEQ = #{caSeq}
