mapper.select:
/* Email.selectContents */
SELECT
EM_SEQ emSeq , DATE_FORMAT(EM_RGST_DT,'%Y.%m.%d') emRgstDt , EM_RGST_ID emRgstId , DATE_FORMAT(EM_RVSE_DT,'%Y.%m.%d') emRvseDt , EM_RVSE_ID emRvseId , EM_TITLE emTitle , EM_CONT emCont , EM_VIEW_CNT emViewCnt , EM_DSGN emDsgn , EM_NEW_YN emNewYn
FROM T_EMAIL WHERE EM_USE_YN='Y' AND EM_SEQ = #{emSeq}

/* Email.selectList */
SELECT
EM_SEQ emSeq , DATE_FORMAT(EM_RGST_DT,'%Y.%m.%d') emRgstDt , EM_RGST_ID emRgstId , DATE_FORMAT(EM_RVSE_DT,'%Y.%m.%d') emRvseDt , EM_RVSE_ID emRvseId , EM_TITLE emTitle , EM_CONT emCont , EM_VIEW_CNT emViewCnt , EM_DSGN emDsgn , EM_NEW_YN emNewYn
FROM T_EMAIL WHERE EM_USE_YN='Y'
ORDER BY EM_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* Email.selectPopList */
SELECT SEQ emUserSeq ,ID emId ,NAME emName ,EMAIL emMail ,IFNULL(NULL, 'ma') userDivn FROM T_AD_USER WHERE USE_YN = 'Y'
SELECT
ME_SEQ emUserSeq , ME_ID emId , ME_NAME emName , ME_MAIL emMail ,IFNULL(NULL, 'ft') userDivn
FROM T_MEMBER WHERE ME_USE_YN = 'Y'
LIMIT #{firstIndex} , #{recordCountPerPage}

/* Email.selectPopCount */
SELECT COUNT(emUserSeq) FROM ( SELECT SEQ emUserSeq ,ID emId ,NAME emName ,EMAIL emMail FROM T_AD_USER WHERE USE_YN = 'Y'
) A WHERE 1 = 1
SELECT COUNT(emUserSeq) FROM ( SELECT
ME_SEQ emUserSeq , ME_ID emId , ME_NAME emName , ME_MAIL emMail
FROM T_MEMBER WHERE ME_USE_YN = 'Y'
) A WHERE 1 = 1

mapper.insert:
/* Email.insertContents */

	INSERT	INTO	T_EMAIL	(
			  EM_RGST_ID 														 
			, EM_TITLE														     
			, EM_CONT 															 
			, EM_DSGN 								 							 
			, EM_NEW_YN 	   													 			  						
	)             
	VALUES	(
			 #{loginSeq     }          
			,#{emTitle      }
			,#{emCont       }
			,#{emDsgn       }
			,#{emNewYn      }
	)

mapper.update:
		/* Email.updateContents */
		
			UPDATE	T_EMAIL
			        
			   SET   
			      
					  EM_RGST_DT 														 = NOW()
					, EM_RVSE_DT 								 						 = NOW()
					, EM_RVSE_ID 														 =#{loginSeq}
					, EM_TITLE														     =#{emTitle}
					, EM_CONT 															 =#{emCont}
					, EM_DSGN 															 =#{emDsgn}
					, EM_NEW_YN 														 =#{emNewYn}
   				     
			 WHERE	EM_SEQ = #{emSeq}

	/* Email.deleteContents */
	   
		UPDATE	T_EMAIL
	       SET 
          	  	EM_USE_YN='N'
		WHERE	EM_SEQ=#{emSeq}
