mapper.select:
/* IpLog.selectContents */
SELECT
IP_SEQ ipSeq , IP_CLIENT_IP ipClientIp , DATE_FORMAT(IP_DT,'%Y.%m.%d') ipDt , IP_RGST_ID ipRgstId , DATE_FORMAT(IP_RGST_DT,'%Y.%m.%d') ipRgstDt
FROM T_IP_LOG WHERE IP_USE_YN='Y' AND IP_SEQ = #{ipSeq}

/* IpLog.selectList */
SELECT
IP_SEQ ipSeq , IP_CLIENT_IP ipClientIp , DATE_FORMAT(IP_DT,'%Y.%m.%d') ipDt , IP_RGST_ID ipRgstId , DATE_FORMAT(IP_RGST_DT,'%Y.%m.%d') ipRgstDt
FROM T_IP_LOG WHERE IP_USE_YN='Y'
ORDER BY IP_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* IpLog.insertContents */
		
			INSERT INTO T_IP_LOG(
           	  	  IP_SEQ														
				, IP_CLIENT_IP	
				, IP_RGST_ID														 
			)    
			VALUES (
				 (SELECT IFNULL(MAX(TF.IP_SEQ)+1,1) FROM T_IP_LOG TF)
				,#{ipClientIp }  
				,#{ipRgstId }  
			)

mapper.update:
/* IpLog.updateContents */

	UPDATE T_IP_LOG        
	 SET   
	      
		 IP_CLIENT_IP													 	 =#{ipClientIp }
		, IP_DT																 = NOW()
		
	WHERE  IP_SEQ = #{ipSeq}

	/* IpLog.deleteContents */
	   
		UPDATE T_IP_LOG 
	       SET
          	 IP_USE_YN='N'
		WHERE IP_SEQ=#{ipSeq}
