sql.if:
      
 				 
					AND #{searchStartDate} <= DATE_FORMAT(LOG_DT, '%Y.%m.%d') AND #{searchEndDate} >= DATE_FORMAT(LOG_DT, '%Y.%m.%d')
				
			
				AND LOG_DIVN = #{schEtc02}
				  
					
						AND (DATE_FORMAT(LOG_DT, '%T') > '18:00:00' OR DATE_FORMAT(LOG_DT, '%T') < '09:00:00')

mapper.select:
/* LogLog.selectList */
SELECT LOG_SEQ logSeq , LOG_CLIENT_IP logClientIp , LOG_ID logId , LOG_URL logUrl , (SELECT MENU_NM FROM t_menu_info B WHERE B.MENU_CD = A.LOG_MENU_CD AND B.MENU_CL = A.LOG_DIVN ) logMenuCd , LOG_DT logDt , LOG_DIVN logDivn , LOG_IP_ERR_YN logIpErrYn
FROM t_log_log A WHERE 1 = 1
AND LOG_IP_ERR_YN = 'Y'
ORDER BY LOG_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

/* LogLog.selectCount */
SELECT COUNT(LOG_SEQ) logCnt FROM T_LOG_LOG WHERE 1 = 1
AND LOG_IP_ERR_YN = 'Y'

mapper.insert:
		/* LogLog.insertContents */
		
			INSERT INTO T_LOG_LOG(
           	  	  LOG_SEQ												
				, LOG_CLIENT_IP 								 				
				, LOG_ID														
				, LOG_URL		
				, LOG_MENU_CD		
				, LOG_DIVN 														
				, LOG_IP_ERR_YN 				 														  						
			)         
			VALUES ( 
				 (SELECT IFNULL(MAX(TL.LOG_SEQ)+1,1) FROM T_LOG_LOG TL)
				,#{logClientIp      }          
				,#{logId	 		}  
				,#{logUrl   	    }
				,#{logMenuCd   	    }
				,#{logDivn          }
				,#{logIpErrYn 		}
			)

mapper.update:
/* LogLog.updateContents */
  
	UPDATE	T_LOG_LOG       
	   SET      
			LOG_CLIENT_IP 								 						 =#{logClientIp}
			, LOG_URL														     =#{logUrl     }
			, LOG_MENU_CD														 =#{logMenuCd  }
			, LOG_DT															 =#{logDt      }
			, LOG_DIVN 															 =#{logDivn    }
			, LOG_IP_ERR_YN 													 =#{logIpErrYn }
		 
	 WHERE  LOG_SEQ = #{logSeq}

	/* LogLog.maResetFailCnt */
	  
		UPDATE	T_AD_USER     
 		   SET
 			  	FAIL_CNT = 0
		 WHERE	ID = #{col2}

/* LogLog.ftResetFailCnt */

	UPDATE	T_MEMBER     
	   SET  
		    ME_FAIL_CNT = 0
	 WHERE  ME_ID = #{col2}

/* LogLog.deleteContents */
    
	UPDATE  T_LOG_LOG
       SET
            ME_FAIL_CNT = 0
     WHERE  LOG_SEQ=#{logSeq}
