mapper.select:
/* Mgr0149.selectList */

	SELECT
		  COM									com
		, AREA									area
		, ADDRESS								address
		, DATE_FORMAT(VISIT_DATE, '%Y.%m.%d')	visitDate
	FROM t_stc VC
	WHERE 1=1       

	
		AND DATE_FORMAT(VC.VISIT_DATE, '%Y') = #{schEtc02}
	
	
	
		
			
				AND DATE_FORMAT(VC.VISIT_DATE, '%Y') = #{schEtc05}
			
			
				AND DATE_FORMAT(VC.VISIT_DATE, '%m') = #{schEtc05}
			
		
	
	
		AND VC.AREA = #{schEtc06}
	
	
		
			ORDER BY VC.VISIT_DATE DESC
		
		
			ORDER BY VC.VISIT_DATE DESC
			LIMIT  #{firstIndex} , #{recordCountPerPage}

/* Mgr0149.selectCount */

	SELECT
		COUNT(VC.COM)		cnt
	FROM t_stc VC
	WHERE 1=1

	
		AND DATE_FORMAT(VC.VISIT_DATE, '%Y') = #{schEtc02}
	
	
	
		
			
				AND DATE_FORMAT(VC.VISIT_DATE, '%Y') = #{schEtc05}
			
			
				AND DATE_FORMAT(VC.VISIT_DATE, '%m') = #{schEtc05}
			
		
	
	
		AND VC.AREA = #{schEtc06}

mapper.insert:
	
        SELECT IFNULL(MAX(CT.CIAP_TOTAL_SEQ)+1,1) FROM t_ciap_total CT
    		
		INSERT INTO t_ciap_total(
			  CIAP_TOTAL_SEQ
			, COM
			, AREA
			, ADDRESS
			, VISIT_DATE
		)
		VALUES 
		
			(
			  #{ciapTotalSeq} + #{index}	
			, #{item.com}
			, #{item.area}
			, #{item.address}
			, STR_TO_DATE(REGEXP_REPLACE(#{item.visitDate},'[^0-9]',''),'%Y%m%d')
			)

mapper.select:
/* Mgr0149.yearSelectList */
SELECT CONCAT(VVC.VISIT_DATE,' ','년') visitDate , VVC.VISIT_DATE visitDateReal , SUM(VVC.AREA1) area1 , SUM(VVC.AREA2) area2 , SUM(VVC.AREA3) area3 , SUM(VVC.AREA4) area4 , SUM(VVC.AREA5) area5 , SUM(VVC.AREA6) area6 , SUM(VVC.AREA7) area7 FROM (SELECT IF(VC.AREA='수도권',1,0) AREA1 , IF(VC.AREA='경상권',1,0) AREA2 , IF(VC.AREA='충청권',1,0) AREA3 , IF(VC.AREA='전라권',1,0) AREA4 , IF(VC.AREA='강원권',1,0) AREA5 , IF(VC.AREA='제주권',1,0) AREA6 , IF(VC.AREA NOT REGEXP '수도권|경상권|충청권|전라권|강원권|제주권', 1,0) AREA7 , DATE_FORMAT(VC.VISIT_DATE,'%Y') VISIT_DATE FROM t_stc VC WHERE 1=1
AND DATE_FORMAT(VC.VISIT_DATE, '%Y') = #{schEtc02}
) VVC GROUP BY VVC.VISIT_DATE ORDER BY VVC.VISIT_DATE DESC

/* Mgr0149.monthSelectList */
SELECT CONCAT(VVC.VISIT_DATE,' ','월') visitDate , VVC.VISIT_DATE visitDateReal , SUM(VVC.AREA1) area1 , SUM(VVC.AREA2) area2 , SUM(VVC.AREA3) area3 , SUM(VVC.AREA4) area4 , SUM(VVC.AREA5) area5 , SUM(VVC.AREA6) area6 , SUM(VVC.AREA7) area7 FROM (SELECT IF(VC.AREA='수도권',1,0) AREA1 , IF(VC.AREA='경상권',1,0) AREA2 , IF(VC.AREA='충청권',1,0) AREA3 , IF(VC.AREA='전라권',1,0) AREA4 , IF(VC.AREA='강원권',1,0) AREA5 , IF(VC.AREA='제주권',1,0) AREA6 , IF(VC.AREA NOT REGEXP '수도권|경상권|충청권|전라권|강원권|제주권', 1,0) AREA7 , DATE_FORMAT(VC.VISIT_DATE,'%m') VISIT_DATE FROM t_stc VC WHERE DATE_FORMAT(VC.VISIT_DATE,'%Y') = #{schEtc02}
) VVC GROUP BY VVC.VISIT_DATE ORDER BY VVC.VISIT_DATE ASC
