mapper.select:
/* Pop.selectContents */
SELECT
PO_SEQ poSeq , PO_ATCH_FILE_ID poAtchFileId , PO_TITLE poTitle , PO_CONT poCont , PO_WIDTH poWidth , PO_HEIGHT poHeight , PO_LEFT poLeft , F_USER_NM(PO_RGST_ID) poRgstId , DATE_FORMAT(PO_RGST_DT,'%Y.%m.%d') poRgstDt
FROM T_POP WHERE PO_USE_YN='Y' AND PO_SEQ = #{poSeq}

mapper.insert:
		/* Pop.insertContents */
		
			INSERT INTO T_POP(
           	 	  PO_SEQ															 
				, PO_ATCH_FILE_ID 													 
				, PO_TITLE														     
				, PO_CONT 															 
				, PO_WIDTH 															 
				, PO_HEIGHT 														 
				, PO_LEFT 		
				, PO_RGST_ID										 
			)   
			VALUES (
				 (SELECT IFNULL(MAX(TF.PO_SEQ)+1,1) FROM T_POP TF)
				,#{ poAtchFileId   }  
				,#{ poTitle        }
				,#{ poCont         }
				,#{ poWidth        }
				,#{ poHeight       }
				,#{ poLeft		   }
				,#{ loginSeq	   }
			)

mapper.update:
/* Pop.updateContents */

	UPDATE T_POP        
	 SET   
	      
		 PO_ATCH_FILE_ID 													=#{poAtchFileId}
		, PO_TITLE														    =#{poTitle     }
		, PO_CONT 															=#{poCont      }
		, PO_WIDTH 															=#{poWidth     }
		, PO_HEIGHT 														=#{poHeight    }
		, PO_LEFT 															=#{poLeft      }
		
	WHERE  PO_SEQ = #{poSeq}

	/* Pop.deleteContents */
	   
		UPDATE T_POP
	       SET
          	 PO_USE_YN='N'
		WHERE PO_SEQ=#{poSeq}
